<?php function factorial($n){if($n<=1){return 1;}return $n*factorial($n-1);}function tribonacci($n,$i,$f1,$f2,$f3){if($n>=0 and $n<3){return $n;}if($i==$n){return $f3;}else{return tribonacci($n,$i+1,$f2,$f3,$f1+$f2+$f3);}}function comb($n,$k){return factorial($n)/(factorial($k)*factorial($n-$k));}function narayana($n,$k){return (comb($n,$k)*comb($n,$k-1))/$n;}function maldad($n){$index=intval(log(narayana($n,intval(log($n,2))),2)+1);return tribonacci($index,2,0,1,2);}$line=intval(trim(fgets(STDIN)));echo"     Este es el resultado: ".strval(maldad($line));?>