//<?php function factorial($n){if($n <= 1){return 1;}return $n*factorial($n-1);}function tribonacci($n){if($n >= 0 and $n<3){return $n;}return tribonacci($n-1)+tribonacci($n-2)+tribonacci($n-3);}function comb($n,$k){return factorial($n)/(factorial($k)*factorial($n-$k));}function narayana($n, $k){return (comb($n,$k)*comb($n,$k-1))/$n;}function maldad($n){$index=intval(log(narayana($n,intval(log($n,2))),2)+1);return tribonacci($index);}$line = intval(trim(fgets(STDIN)));echo"     Este es el resultado: ".strval(maldad($line));?>